operation.add:
$this->load->model('account/order');
    if (isset($this->session->data['order_id'])) {
    $data['bancot'] = true;
    $order_info = $this->model_account_order->getOrder($this->session->data['order_id']);

		if ($order_info && $order_info['payment_code'] == 'bank_transfer') {
    $query = $this->db->query("SELECT * FROM `" . DB_PREFIX . "order_history` WHERE order_id = '" . (int)$order_info['order_id'] . "'");
    $data['instr'] = $query->row['comment'];
    } else {
    $data['bancot'] = false;
    $data['instr'] = "";
    }

    } else {
    $data['bancot'] = false;
    }
